# takes description of two wines and returns normalized similarity
def sim_wines(w1, w2):
	# string to array
	grapes1 = w1.split()
	grapes2 = w2.split()

	# maximum total score
	max_g = max(len(grapes1), len(grapes2))

	# calculate similarity for all grapes
	tot_sim = 0
	for grape in grapes1:
		for g in grapes2:
			tot_sim += sim_grapes(grape, g)

	# calc normalized similarity
	if max_g > 0:
		sim = tot_sim/max_g
	else: 
		sim = tot_sim

	return sim

# takes two types of grapes and returns their similarity measure
def sim_grapes(w1, w2):
	if w1 is None or w2 is None:
		#print("No type of wine given...")
		return 0
	else:
		if w1 in lookup:
			#print("found first wine!")
			comp = lookup[w1]
			for item in comp:
				#print(item)
				if w2 in item:
					#print("found second wine!")
					return item[w2]
			return 0
		else: 
			#print("This wine is not in the list...")
			return 0

